// filter function start here

myApp.filter('outcome_maker', function () {
  return function(input) {
    if (input.maker_outcome_id == 1) {
      return "Rival bets " + input.fixture.home_team.team_name + " wins";
    } 

    else if (input.maker_outcome_id == 2) {
      return "Rivals bets " + input.fixture.home_team.team_name + " ties " +  input.fixture.abroad_team.team_name;
    }

    else if (input.maker_outcome_id == 3) {
      return "Rival bets " + input.fixture.abroad_team.team_name + " wins";
    }
  }
});


myApp.filter('outcome_taker', function () {
  return function(input) {
    if (input.taker_outcome_id == 1) {
      return input.fixture.home_team.team_name + " wins.";
    } 

    else if (input.taker_outcome_id == 2) {
      return input.fixture.home_team.team_name + " ties " +  input.fixture.abroad_team.team_name + ".";
    }

    else if (input.taker_outcome_id == 3) {
      return input.fixture.abroad_team.team_name + " wins";
    }
  }
});


myApp.filter('countdown_init', function () {
  return function(input) {
    expires_at = new Date(input.expiration_time);
   // console.log(expires_at);
    now = new Date();
   // console.log(now);
   // console.log(expires_at - now);
    return secondsToString((expires_at - now) / 1000);
  }
});

myApp.filter('expiration_time_attr', function () {
  return function(input) {
    return expires_at = new Date(input);
  }
});



// Utility function

// converts the coundown into days:hours:minutes:seconds

function secondsToString(seconds)
{

var numdays = Math.floor(seconds / 86400);
var numhours = Math.floor((seconds % 86400) / 3600);
var numminutes = Math.floor(((seconds % 86400) % 3600) / 60);
var numseconds = Math.floor((seconds % 86400) % 3600) % 60;

return numdays + " days " + numhours + " hours " + numminutes + " minutes " + numseconds + " seconds";

}


// trigger function to update countdown every second or scope


(function(){
     update_countdown = function() {
       $('[id^="bettle_countdown_"]').each(function() {
       	 // console.log($(this).attr('expiration-time'));  
         var now = new Date();
         var expires_at = new Date( $(this).attr('expiration-time').substring(1, $(this).attr('expiration-time').length-1 ));
         // destroy element if bet expired
         if (now > expires_at) {
         	$(this).parent().remove();
         }
         $(this).text('Countdown: ' + secondsToString((expires_at - now) / 1000));
       })
    }
  
    setInterval("update_countdown()", 1000);
})();